function LoadLeatherArr(data) {
    MixMatch.Leather = data.leather;
    MixMatch.front = data.front;
    MixMatch.side = data.side;
    MixMatch.back = data.back;
    MixMatch.lining = data.lining;
    MixMatch.laces = data.laces;
    MixMatch.accessory = data.accessory;
}

function load_leather_xml(data) {
    listEle.listLth = data;
    load_leather_menu('sub-menu-leather');
}
